const STUDENT =
  "";

const PROFESSOR =
  "";

const ADMIN =
  "";

export const getRole = (role: string): string => {
  if (role === ADMIN) {
    return "ADMIN";
  } else if (role === PROFESSOR) {
    return "PROFESSOR";
  } else {
    return "STUDENT";
  }
};

export const decodeRole = (role: string): string => {
  if (role === "ADMIN") {
    return ADMIN;
  } else if (role === "PROFESSOR") {
    return PROFESSOR;
  } else if (role === "STUDENT") {
    return STUDENT;
  } else {
    return "";
  }
};

interface Obj {
  rollNo: number;
  firstName: string;
  middleName: string;
  lastName: string;
  email: string;
  phoneNo: number;
  role: string;
  department_name: string;
}

export const customUserList = (arr: Obj[]): Obj[] => {
  let list: Obj[];
  list = arr?.map((i: Obj) => {
    return { ...i, role: getRole(i.role) };
  });
  return list;
};
